import React from 'react'
import './articlepage.styes.scss';
import {data} from '../../data/index';

import parse from 'html-react-parser';
import RelatedArticles from '../../components/relatedArticles/relatedArticles.component';
// function Ad(){
//     return(
//         <Fragment></Fragment>
//     )
// }



function Articlepage({match}) {

    const {category , slug} = match.params;
    const title = slug.replaceAll("-"," ").replaceAll("=","?");
    let article = null;


    data.forEach(item => {
        if(item.title === title && category === item.category) article = item;
    }) 


    const nv = article.articleContent.split("GOOGLEADSENSE");
    console.log(nv);




    return (
        <div className="articlepage container">
        <h1>{article.title}</h1>
            <img  src={`/images/${article.id}.jpeg`} alt={article.title}/>
        <div className="articlepage__content">

        {parse(article.articleContent)}


        </div>
        <RelatedArticles />
        </div>
    )
}

export default Articlepage
